    };
    
    struct IAnimal {
      template&lt;class T&gt;
      IAnimal(T&amp; t) : MakeSound{ [&amp;t]{return t.MakeSound();}}
      {}
    
      std::function&lt;const char*()&gt; MakeSound;
    };
    
    int main() {   
        Dog dog;   
        Duck duck;   
        IAnimal animal = dog;  
        std::cout &lt;&lt; animal.MakeSound() &lt;&lt; std::endl; // prints woof   
        animal = duck;  
        std::cout &lt;&lt; animal.MakeSound() &lt;&lt; std::endl; // prints quack   
        return 0; 
    }; 

