    reserved = reserved &lt;= 8 ? 8 : 1 &lt;&lt; (32 - __builtin_clz(reserved));
    int *data = (int *) malloc(reserved);
    if (!data) return FALSE;
    out-&gt;size = 0;
    out-&gt;reserved = reserved;
    out-&gt;data = data;
    return TRUE;
}

size_t vector_size(int_vector_t *vec) {
    return vec-&gt;size;
}

int vector_get(int_vector_t *vec, size_t index) {
