    if (vec-&gt;size == vec-&gt;reserved) {
        if (!resize(vec, vec-&gt;reserved &lt;&lt; 1)) return FALSE;
    }
    vec-&gt;data[vec-&gt;size++] = value;
    return TRUE;
}

int vector_pop(int_vector_t *vec, int *value) {
    if (!vec-&gt;size) return FALSE;
    vec-&gt;size--;
    *value = vec-&gt;data[vec-&gt;size];
    if (vec-&gt;size == (vec-&gt;reserved &gt;&gt; 2) &amp;&amp; vec-&gt;reserved &gt; 8) {
