        Console.ReadKey();

        Stopwatch sw = Stopwatch.StartNew();
        Console.WriteLine(&quot;Timer started. Press any key to stop&quot;);
        Console.ReadKey();

        sw.Stop();
        Console.WriteLine(&quot;The timer has stopped.&quot;);
        Console.WriteLine($&quot;Elapsed time is: {sw.Elapsed.TotalSeconds} seconds.&quot;);
        Console.ReadLine();
    }

