                if line.startswith(&quot;&gt;&quot;):
                    if name: yield (name, &#39;&#39;.join(seq))
                    name, seq = line, []
                else:
                    seq.append(line)
            if name: yield (name, &#39;&#39;.join(seq))
    
    with open(&#39;protein.fasta&#39;) as fp:
