In modern browsers you can use SVG filters on canvas drawings, from which the [feConvolveMatrix](https://developer.mozilla.org/en-US/docs/Web/SVG/Element/feConvolveMatrix) will make it quite straight-forward:

&lt;!-- begin snippet: js hide: false console: true babel: false --&gt;

&lt;!-- language: lang-js --&gt;

    const canvas = document.getElementById( &#39;canvas&#39; );
    const ctx = canvas.getContext( &#39;2d&#39; );
    const matrix = document.getElementById( &#39;conv-matrix&#39; );
    let speed = 0.05;
    let pow = 0;
    const img = new Image();
    img.onload = (e) =&gt; {
      canvas.width = img.width;
      canvas.height = img.height;
      animate();
    };
    img.src = &quot;https://upload.wikimedia.org/wikipedia/commons/5/55/John_William_Waterhouse_A_Mermaid.jpg&quot;;

    function animate() {
      // animate &#39;pow&#39; back and forth
      pow = pow + speed;
      if( pow &gt;= 5 || pow &lt;= -2 ) { speed *= -1; }
      // we&#39;ll use the SVG filter on our context
      ctx.filter = &#39;url(#sharp)&#39;;
      // set the matrix as you wish, here I just gather all closest to the center
      matrix.setAttribute( &#39;kernelMatrix&#39;,
        pow * 1 + &#39; &#39; + pow *  1 + &#39; &#39; + pow * -1 + &#39; &#39; +
        pow * 1 + &#39; &#39; + 1 + &#39; &#39; + pow * -1 + &#39; &#39; +
        pow * 1 + &#39; &#39; + pow * -1 + &#39; &#39; + pow * -1
      );

      ctx.drawImage( img, 0, 0 );
      ctx.filter = &#39;none&#39;;
      
      requestAnimationFrame( animate );
    }

&lt;!-- language: lang-html --&gt;

    &lt;svg width=&quot;0&quot; height=&quot;0&quot; style=&quot;position:absolute;z-index: -1&quot;&gt;
      &lt;filter id=&quot;sharp&quot;&gt;
        &lt;feConvolveMatrix id=&quot;conv-matrix&quot;
          kernelMatrix=&quot;
             1  1 -1
             1  1 -1 
             1 -1 -1
         &quot;/&gt;
      &lt;/filter&gt;
    &lt;/svg&gt;
    &lt;canvas id=&quot;canvas&quot;&gt;&lt;/canvas&gt;

&lt;!-- end snippet --&gt;


----------

