&lt;!-- language: lang-js --&gt;
    const
        pipe = (...functions) =&gt; input =&gt; functions.reduce((acc, fn) =&gt; fn(acc), input),
        groupBy = key =&gt; array =&gt; array.reduce((r, o) =&gt; {
            var temp = r.find(([p]) =&gt; o[key] === p[key])
            if (temp) temp.push(o);
