    function renderTable(data) {
        var result = [&#39;&lt;table&gt;&#39;];
        var header = false;
        for (var index in data) {
            var row = data[index];
            if (!header) {
                // Create header row.
                header = Object.keys(row);
                var res = [&#39;&lt;tr&gt;&#39;];
                for (var r in header) {
                    res.push(&quot;&lt;th&gt;&quot; + header[r] + &quot;&lt;/th&gt;&quot;);
                }
                res.push(&#39;&lt;/tr&gt;&#39;);
                result.push(res.join(&quot;\n&quot;));
            }
            // Add data row.
            var res = [&#39;&lt;tr&gt;&#39;];
            for (var r in header) {
                res.push(&quot;&lt;td&gt;&quot; + row[header[r]] + &quot;&lt;/td&gt;&quot;);
            }
            res.push(&#39;&lt;/tr&gt;&#39;);
            result.push(res.join(&quot;\n&quot;));
        }
        result.push(&#39;&lt;/table&gt;&#39;);
        return result.join(&quot;\n&quot;);
    }
