      rob: &quot;javascript&quot;,
      patrick: [&quot;javascript&quot;, &quot;react&quot;, &quot;python&quot;]
    }

    const output = {}

    for (const name in namesAndSkills) {
      for (const skill of [].concat(namesAndSkills[name])) {
        if (output[skill])
          output[skill] = [].concat(output[skill], name)
        else
          output[skill] = name;
      }
    }

    console.log(output)

&lt;!-- end snippet --&gt;


