                          JOIN DEPT T2
                            ON T1.DEPTNO = T2.DEPTNO
                         WHERE DNAME = &#39;SALES&#39;
                           AND JOB NOT LIKE &#39;MANAGER&#39;
                        ) tt
                     WHERE e.EMPNO = tt.EMPNO );

