    select t.*,
           (case when event_type = &#39;UP&#39; then &#39;DOWN&#39; else &#39;UP&#39; 
            end) as missing_event_type
    from (select t.*,
                 lag(event_type) over (order by datetime) as prev_event_type
          from t
         ) t
    where prev_event_type &lt;&gt; event_type;

