    	}


    }

    public void sort() {
    	buildMaxHeap();
    	int size = HeapArray.length - 1;
    	for(int i = size; i &gt;= 2 ; i--) {
    		swap(HeapArray[1], HeapArray[i]);
    		maxHeapify(1, i - 1);

    	}

    }



    // Swap two nodes of the heap at index first second
    private void swap(int first, int seconds)
    {
        int tmp;
