A brute force method. Time complexity might be O(N^2). Break into about four steps:&lt;br/&gt;
1. Input loop(&#39;\n&#39; will finished one string input)&lt;br/&gt;
2. Split string line into words list.&lt;br/&gt;
3. Count every word time in list. (Brute force)&lt;br/&gt;
4. Print result.&lt;br/&gt;

    #include &lt;stdio.h&gt;
    
    #include &lt;string.h&gt;
    #include &lt;stdlib.h&gt;
    #include &lt;stdbool.h&gt;
    
    #define MAXSTRLEN 1024
    
    char **splitToList(char *a, int *rowSize) {
        char **b=NULL;
        int start=0;
        int count=0;
        for (int i = 0; i &lt; strlen(a); ++i) {
            if (&#39; &#39; == a[start]) {
                start = i;
                continue;
            }
    
            if (&#39; &#39; == a[i]) {
                ++count;
                if (NULL == b) {
                    b = (char **)malloc(sizeof(char *));
                    b[count-1] = (char *)malloc(sizeof(char)*(i-start+1));
                    memcpy(b[count-1], &amp;a[start], (i-start));
                    b[count-1][i-start] = &#39;\0&#39;;
                    start = i;
                } else {
                    b = (char **)realloc(b, sizeof(char *)*(count));
                    b[count-1] = (char *)malloc(sizeof(char)*(i-start+1));
                    memcpy(b[count-1], &amp;a[start], (i-start));
                    b[count-1][i-start] = &#39;\0&#39;;
                    start = i;
                }
                continue;
            }
    
        }
    
        if (start &lt;= strlen(a) &amp;&amp; a[strlen(a)-1] != &#39; &#39;) {
            ++count;
            b = (char **)realloc(b, sizeof(char *)*(count));
            b[count-1] = (char *)malloc(sizeof(char)*(strlen(a)-start+1));
            memcpy(b[count-1], &amp;a[start], (strlen(a)-start));
            b[count-1][strlen(a)-start] = &#39;\0&#39;;
        }
    
        *rowSize = count;
    
        return b;
    }
    
    bool equalChars(char *a, char *b) {
        if (strlen(a) != strlen(b)) {
            return false;
        }
    
        int i=0;
        while (i&lt;strlen(a) &amp;&amp; a[i] == b[i]) {
            ++i;
        }
    
        if (i &lt; strlen(a)) {
            return false;
        }
    
        return true;
    }
    
    void countWords(char *a) {
        int *rowSize = (int *)malloc(sizeof(int));
    
        // 1. split to word list
        char **b = splitToList(a, rowSize);
    
        // 2. count words times
        int count[*rowSize];
        for (int i = 0; i &lt; *rowSize; ++i) {
            if (0 == i) {
                count[i] = 1;
                continue;
            }
            int dup = false;
            for (int j = 0; j &lt;= i-1 ; ++j) {
                if (equalChars(b[i], b[j])) {
                    count[j] = count[j] + 1;
                    count[i] = 0;
                    dup = true;
                    break;
                }
            }
    
            if (!dup) {
                count[i] = 1;
            }
        }
    
        // 3. print words times
        for (int i = 0; i &lt; *rowSize; ++i) {
