const bindFn = &lt;T extends {[key: string]: Function}&gt;(
  obj: T,
  wrapFn: Function
) =&gt; {
  return Object.entries(obj).reduce((carry, [key, fn]) =&gt; {
    carry[key] = (...args) =&gt; wrapFn(fn.apply(null, args));
    return carry;
  }, {}) as T;
};

