```kotlin
fun &lt;T : Image&gt; getNewImages(input: ImageInput, offset: Int, limit: Int = Int.MAX_VALUE): Images&lt;T&gt; {
  return when (input.imageType) {
    ImageEnum.NUMBER -&gt;
      numberImageService.getNewImages(offset, limit)
