    private static func video(fromJSON json: [String: Any]) -&gt;Video? {
        let idJson = json[&quot;id&quot;] as? [String: Any]
        let snippetJson = json[&quot;snippet&quot;] as? [String: Any]
        let thumbnail = json[&quot;thumbnails&quot;] as? [String: Any]
        let medium = thumbnail?[&quot;medium&quot;] as? [String: Any]

        guard
            let videoID = idJson?[&quot;videoId&quot;] as? String,
            let title = snippetJson?[&quot;title&quot;] as? String,
            let description = snippetJson?[&quot;description&quot;] as? String,
            let stringURL = medium?[&quot;url&quot;] as? String,
            let url = URL(string: stringURL)
            else {
                return nil
        }
        return Video(videoID: videoID, title: title, description: description, url: url)
    }


