    public function findPairsInArrayWhereSum($sum, $arr = [])
    {
        $s = [];
        $evenlyDistributedArrays = [];
        for ($i = 0; $i &lt; count($arr); $i++) {
            $temp = $sum - $arr[$i];
            $s[] = $arr[$i];
            if (in_array($temp, $s)) {
                $evenlyDistributedArray[] = [$arr[$i], $temp];
            }
        }
        return $evenlyDistributedArray;
    }

