    public interface IEntity&lt;out T&gt; : IEntity
    {
        T Id { get; }
    }

    public abstract class EntityBase&lt;T&gt; : IEntity&lt;T&gt;
    {
        public T Id { get; private set; }
    }

    public interface IRepository&lt;TEntity&gt; where TEntity: IEntity
    {
    }

