/**
 * Register the [woocommerce_recently_viewed_products per_page=&quot;5&quot;] shortcode
 *
 * This shortcode displays recently viewed products using WooCommerce default cookie
 * It only has one parameter &quot;per_page&quot; to choose number of items to show
 *
 * @access      public
 * @since       1.0 
 * @return      $content
*/
function rc_woocommerce_recently_viewed_products( $atts, $content = null ) {

	// Get shortcode parameters
	extract(shortcode_atts(array(
		&quot;per_page&quot; =&gt; &#39;5&#39;
	), $atts));

	// Get WooCommerce Global
	global $woocommerce;

	// Get recently viewed product cookies data
	$viewed_products = ! empty( $_COOKIE[&#39;woocommerce_recently_viewed&#39;] ) ? (array) explode( &#39;|&#39;, $_COOKIE[&#39;woocommerce_recently_viewed&#39;] ) : array();
	$viewed_products = array_filter( array_map( &#39;absint&#39;, $viewed_products ) );

	// If no data, quit
	if ( empty( $viewed_products ) )
		return __( &#39;You have not viewed any product yet!&#39;, &#39;rc_wc_rvp&#39; );

	// Create the object
	ob_start();

	// Get products per page
	if( !isset( $per_page ) ? $number = 5 : $number = $per_page )

	// Create query arguments array
    $query_args = array(
    				&#39;posts_per_page&#39; =&gt; $number, 
    				&#39;no_found_rows&#39;  =&gt; 1, 
    				&#39;post_status&#39;    =&gt; &#39;publish&#39;, 
    				&#39;post_type&#39;      =&gt; &#39;product&#39;, 
    				&#39;post__in&#39;       =&gt; $viewed_products, 
    				&#39;orderby&#39;        =&gt; &#39;rand&#39;
    				);

	// Add meta_query to query args
	$query_args[&#39;meta_query&#39;] = array();

    // Check products stock status
    $query_args[&#39;meta_query&#39;][] = $woocommerce-&gt;query-&gt;stock_status_meta_query();

	// Create a new query
	$r = new WP_Query($query_args);

	// If query return results
	if ( $r-&gt;have_posts() ) {

		$content = &#39;&lt;ul class=&quot;rc_wc_rvp_product_list_widget&quot;&gt;&#39;;

		// Start the loop
		while ( $r-&gt;have_posts()) {
			$r-&gt;the_post();
			global $product;

			$content .= &#39;&lt;li&gt;
				&lt;a href=&quot;&#39; . get_permalink() . &#39;&quot;&gt;
					&#39; . ( has_post_thumbnail() ? get_the_post_thumbnail( $r-&gt;post-&gt;ID, &#39;shop_thumbnail&#39; ) : woocommerce_placeholder_img( &#39;shop_thumbnail&#39; ) ) . &#39; &#39; . get_the_title() . &#39;
				&lt;/a&gt; &#39; . $product-&gt;get_price_html() . &#39;
			&lt;/li&gt;&#39;;
		}

		$content .= &#39;&lt;/ul&gt;&#39;;

	}

	// Get clean object
	$content .= ob_get_clean();
	
	// Return whole content
	return $content;
}

// Register the shortcode
