    const outputs = {
      normal: document.getElementById(&quot;normaltimeOutput&quot;),
      over: document.getElementById(&quot;overtimeOutput&quot;),
      total: document.getElementById(&quot;totalOutput&quot;)
    };
    const inputs = {
      normal: document.getElementById(&quot;normaltimeInput&quot;),
      over: document.getElementById(&quot;overtimeInput&quot;)
    };

    function update(){

      outputs.normal.innerHTML = inputs.normal.value * 900;
      outputs.over.innerHTML = inputs.over.value * 1125;
      outputs.total.innerHTML = inputs.normal.value * 1125 + inputs.over.value * 1125;
      
    }

    for( let input in inputs ){
      
      inputs[input].addEventListener( &#39;input&#39;, update );
      
    }

