        {level: 25, source: require(&quot;./../../../android/app/src/main/res/drawable/batterylevel_two&quot;)},
        {level: 0,  source: require(&quot;./../../../android/app/src/main/res/drawable/batterylevel_one&quot;)}
    //          ^---- or possibly `-Infinity`, see note below
    ];

then it&#39;s

