        ]);

        var view = new google.visualization.DataView(data);
        view.setColumns([0, 1, {
            calc: &quot;stringify&quot;,
            sourceColumn: 1,
            type: &quot;string&quot;,
            role: &quot;annotation&quot;
        }, 3, 4, 5]);

        var options = {
            title: &#39;&#39;,
            width: &#39;100%&#39;,
            height: 132,
            chartArea: {
              height: &#39;100%&#39;,
              width: &#39;100%&#39;,
              top: 36,
              left: 18,
              right: 18,
              bottom: 48
            },
            hAxis: {
                title: &#39;&#39;,
                minValue: 0,
                gridlines: {
                    count: 6
                },
                format: &#39;0&#39;
            },
            bar: {
                groupWidth: &quot;60%&quot;
            },
            legend: {
                position: &quot;top&quot;
            },
            series: {
                0: {
                    color: &#39;#70b5c5&#39;,
                    visibleInLegend: false
                }, // Goal Achieved
                1: {
                    color: &#39;#000000&#39;,
                    type: &#39;line&#39;,
                    annotations: {
                        textStyle: {
                            color: &#39;#000000&#39;
                        },
                        stem: {
