    function toggleVisibility(elementId) {
      var element = document.getElementById(elementId);
      if (element.style.height === &quot;0&quot;) {
        element.classList.remove(&#39;hidden&#39;);
      } else {
        element.classList.add(&#39;hidden&#39;);
      }
    }

