    public function searchByName($name)
    {
        $company = Company::where(&#39;name&#39;, $name)-&gt;firstOrFail();
        $personality = DB::table(&#39;personalities&#39;)-&gt;where(&#39;name&#39;, $name)-&gt;first();
    
        return view(&#39;company.base.index&#39;, compact(&#39;company&#39;, &#39;name&#39;, &#39;personality&#39;));
    }


