def formatter(s):
    &quot;&quot;&quot;Convert binary string s to hex, but keep leading zeroes.&quot;&quot;&quot;
    zeroes = re.match(r&#39;0*&#39;, s)[0]
    binary = s[len(zeroes):]
    i = int(binary, 2)
    return &#39;{}{:x}&#39;.format(zeroes, i)

x = &#39;001 10100 00010 1100100 1000&#39;
