      let [isWaiting, setWaiting ] = useState(false)

      let meteorMethod = () =&gt; {
          setWaiting(true)
          Meteor.call(&#39;insertSometing&#39;, data,(result,error) =&gt; {
            if(error) 
            console.log(&#39;erro&#39;)
            else
            setWaiting(false)
          })
      }


      // If isWaiting is true the button disable and if false unable
