      &lt;script id=&quot;myView&quot; type=&quot;sapui5/xmlview&quot;&gt;
        &lt;mvc:View xmlns:mvc=&quot;sap.ui.core.mvc&quot; xmlns=&quot;sap.m&quot; controllerName=&quot;myController&quot;&gt;

          &lt;HBox id=&quot;toggleButtons1&quot; fitContainer=&quot;true&quot; class=&quot;fullWidthButtons&quot;&gt;
