              &lt;/layoutData&gt;
            &lt;/ToggleButton&gt;
          &lt;/HBox&gt;

          &lt;Button text=&quot;Previous&quot; press=&quot;onPressPrevious&quot; enabled=&quot;true&quot; /&gt;
