        &lt;div v-for=&quot;product in productsWithHeadlines&quot; :key=&quot;product.id&quot;&gt;
          &lt;div&gt;{{ product.name }}&lt;/div&gt;
          &lt;ul&gt;
            &lt;li v-for=&quot;(headline, index) in product.headlines&quot; :key=&quot;index&quot;&gt;
              &lt;div&gt;{{ headline.text }}&lt;/div&gt;
              &lt;div&gt;{{ headline.feature }}&lt;/div&gt;
