        /the/g, // search for all (g = global) &quot;the&quot; in the innerHTML
        &#39;&lt;span class=&quot;alert&quot;&gt;$&amp;&lt;/span&gt;&#39; // replace/wrap the &quot;the&quot; with some tags and class
