 * Description: Your plugin description
 * Version:     1.0
 * Author:      Fahad Shaikh
 * Author URI:  https://example.com
*/

require_once __DIR__ . &#39;/vendor/autoload.php&#39;;

use inc\Activate;

class FahadPlugin {
  function activate() {
    Activate::activate();
  }
}

$fp = new FahadPlugin();
register_activation_hook(__FILE__, array($fp, &#39;activate&#39;));
```

