    for (int i = 0; i &lt; str.length(); i++) {
        char c = str.charAt(i);
        if (charCount.containsKey(c)) {
            charCount.put(c, charCount.get(c)+1);
        } else {
            charCount.put(c, 1);
        }
    }
        
