                for (String dir: dirList){
                    if(!(dir.equals(&quot;emulated&quot;) || dir.equals(&quot;self&quot;))){
                        File file = new File( &quot;/storage/&quot; + dir + &quot;/DCIM/&quot; + &quot;mm.png&quot;);
                        if(file.exists()) {
                            Bitmap picture = BitmapFactory.decodeFile(file.getAbsolutePath());
                            imageView.setImageBitmap(picture);
    
                            break;
                        }
    
    
                    }
                }
            }
    
        }
    }

