    csvWriter.append(&quot;\n&quot;);
    
    for (List&lt;String&gt; rowData : rows) {
        csvWriter.append(String.join(&quot;,&quot;, rowData));
        csvWriter.append(&quot;\n&quot;);
    }
    
    csvWriter.flush();
    csvWriter.close();


