    &lt;xsl:stylesheet xmlns:xsl=&quot;http://www.w3.org/1999/XSL/Transform&quot;
    	xmlns:xs=&quot;http://www.w3.org/2001/XMLSchema&quot;
    	xpath-default-namespace=&quot;http://www.tei-c.org/ns/1.0&quot;
