void PrintScientific(long double d) {
    int e = 0;
    if(d &lt; 1 &amp;&amp; d &gt; -1) {
        for(e = 0; d*10 &lt; 10 &amp;&amp; d*10 &gt; -10; e--) {
            d *= 10;
        }
    } else {
       for(e = 0; d/10 &gt; 1 || d/10 &lt; -1; e++) {
            d /= 10;
        }
        
    }

    std::cout &lt;&lt; std::setprecision(4) &lt;&lt; std::fixed &lt;&lt; d &lt;&lt; &quot;*10^&quot; &lt;&lt; e;
}
