    public static void main(String[] args) {
        int[] nums = new int[]{12,11,5,2,12};
        System.out.println(isPair(nums));
    }

    private static String isPair(int[] nums){
        Set&lt;Integer&gt; set = new HashSet&lt;&gt;();
        for(int n : nums){
            if(set.contains(n)){
                return &quot;PAIR&quot;;
            }else{
                set.add(n);
            }
        }
        return null;
    }

