      //draw a circle
      ctx.beginPath();
      ctx.arc(center_x, center_y, radius, 0, 2 * Math.PI);
      ctx.stroke();

      analyser.getByteFrequencyData(frequency_array);
      var i;
      for (i = 0; i &lt; bars; i++) {

        //divide a circle into equal parts
