        //draw a bar
        var lineColor = &quot;rgb(&quot; + frequency_array[i] + &quot;, &quot; + frequency_array[i] + &quot;, &quot; + 205 + &quot;)&quot;;

        ctx.strokeStyle = lineColor;
        ctx.lineWidth = bar_width;
        ctx.beginPath();
        ctx.moveTo(x, y);
        ctx.lineTo(x_end, y_end);
        ctx.stroke();

      }

      renderVizualitationID = requestAnimationFrame(animationLooper);
    }

