      if (renderVizualitationID) cancelAnimationFrame(renderVizualitationID);

      audio.pause();
      audio.src = &#39;&#39;;
      frequency_array.fill( 0 );
      ctx.clearRect(0, 0, canvas.width, canvas.height);
      // kill the previous one
