        ListConsumerGroupOffsetsResult offsets = client.listConsumerGroupOffsets(&quot;consumerId&quot;);
        Map&lt;TopicPartition, OffsetAndMetadata&gt; tt = offsets.partitionsToOffsetAndMetadata().get();
