        //&lt;&lt;&lt;&lt;&lt;&lt;&lt;&lt;&lt;&lt; SEARCH The searchable columns &gt;&gt;&gt;&gt;&gt;&gt;&gt;&gt;&gt;
        @Query(&quot;SELECT bible.book,bible.chapter,bible.verse,bible.content FROM bible_fts JOIN bible ON id = docid WHERE bible_fts MATCH :searchFor&quot;)
