              as: &quot;ui&quot;,
              in: {
                $mergeObjects: [
                  &quot;$$ui&quot;,
                  {
                    $arrayElemAt: [
                      {
                        $filter: {
                          input: &quot;$users&quot;,
                          as: &quot;users&quot;,
                          cond: {
                            $eq: [
                              &quot;$$users.user_id&quot;,
                              &quot;$$ui._id&quot;
                            ]
                          }
