    .list-item-growing {
        position: relative;
        height: 260px;
        overflow: hidden;
        background: #fff;
    }

    .list-item-growing .image-container {
        position: absolute;
        top: 0;
        left: 0;
        z-index: 1;
    }

    .list-item-growing .image-container .list-item-image {
        /* max-height: 100%; */
        transition: transform 60s ease;
    }

    .list-item-growing:hover .image-container .list-item-image {
        transform: scale(2);
    }

    .list-item-growing .image-container .image-wrapper {
        width: 150%;
    }

    .list-item-growing .headline-container {
        position: absolute;
        bottom: 0;
        left: 0;
        padding: 10px 20px;
        height: 33%;
        background: rgba(255,255,255,0.9);
        width: 100%;
        display: flex;
        text-align: center;
        justify-content: center;
        align-items: center;
        z-index: 2;
    }

    .list-item-growing .excerpt-container {
        position: absolute;
        top: 15px;
        left: 15px;
        right: 15px;
        padding: 20px;
        bottom: calc(33% + 15px);
        background: rgba(105,20,205,0.75);
        color: #fff;
        border-radius: 5px;
        border: 1px solid #fff;
        display: flex;
        justify-content: center;
        align-items: center;
        transition: 0.25s;
        overflow: hidden;
        opacity: 0;
        z-index: 3;
    }

    .list-item-growing:hover .excerpt-container {
        opacity: 1;
    }

    @media only screen and (min-width: 768px) {
        .list-item-growing .headline-container {
            height: 50%;
        }
    }

    @media only screen and (min-width: 1280px) {
        .list-item-growing .headline-container {
