&lt;!-- language: lang-js --&gt;

    function NeoElement() {
        var ref = Reflect.construct(HTMLElement,[], this.constructor) ;
        return ref;
    };
    NeoElement.prototype = Object.create(HTMLElement.prototype);
    NeoElement.prototype.constructor = NeoElement;
