    ];

    arr.forEach(a =&gt; {
      console.log(`n = ${a}, str = ${getFirstNonZeroStr(a)}, log = ${getFirstNonZeroLog(a)}`);
    });

    function getFirstNonZeroStr(n) {
      if (n &gt;= 1) return -1;
      let s = n.toString();
      let m = s.match(/^0\.(0*)[\d]+$/);
      return m[1].length;
    }

    function getFirstNonZeroLog(n) {
      if (n &gt;= 1) return -1;
      return Math.floor(Math.abs(Math.log10(n)));
    }

