        document.querySelector(&#39;#days&#39;).innerHTML += &#39;&lt;li style=&quot;opacity: 0.5;&quot;&gt;&#39; + (lastMonthDays - i) + &#39;&lt;/li&gt;&#39;;
      }
      // fill the current month days
