    @JsonIgnoreProperties(ignoreUnknown = true)
    @Getter
    @Setter
    @NoArgsConstructor
    public class TimeSeries {
    
        @JsonProperty(&quot;Meta Data&quot;)
        private MetaData metaData;
    
