            var ps = phrases[0];
            var psNext = phrases[1];
            return ps.Kanji != psNext.Kanji || ps.Kana != psNext.Kana ||
                ps.English.Length &gt; psNext.English.Length;
        }
        else // The last is a single phrase
        {
            return true;
        }
    })
    .Select(window =&gt; window[0]) // Select the first phrase
    .ToList();

