    //Meyer&#39;s singleton
    class MySingleton{
    public:
      static MySingleton&amp; getInstance(){
        static MySingleton instance;
        // volatile int dummy{};
        return instance;
      }
    private:
      MySingleton()= default;
      ~MySingleton()= default;
      MySingleton(const MySingleton&amp;)= delete;
      MySingleton&amp; operator=(const MySingleton&amp;)= delete;
    
    };


    //with call_once
    class MySingleton{
    public:
      static MySingleton&amp; getInstance(){
        std::call_once(initInstanceFlag, &amp;MySingleton::initSingleton);
        // volatile int dummy{};
        return *instance;
      }
    private:
      MySingleton()= default;
      ~MySingleton()= default;
      MySingleton(const MySingleton&amp;)= delete;
      MySingleton&amp; operator=(const MySingleton&amp;)= delete;
    
      static MySingleton* instance;
      static std::once_flag initInstanceFlag;
    
